<template>
	<!-- <view> -->
	<!-- <view class="z-refresh-full" v-show="isEnd == 1"></view> -->
	<view class="z-refresh-view" :animation="animationData" :style="isTranform">
		<view class="refresh" :style="isZoom" :class="isEnd==2?'z-refresh-animation':''">
			<view class="z-refresh-word" v-if="isEnd == 0">{{tip}}</view>
			<view class="z-refresh-cirle z-animation-rotate" v-if="isEnd == 1"></view>
			<image
				class="z-refresh-icon-ok"
				src="data:image/png;base64,"
				v-if="isEnd==2"
			/>
		</view>
		<!-- </view> -->
	</view>
</template>

<script>
// @touchstart="touchstart" @touchmove="touchmove" @touchend="touchend"
let animation = uni.createAnimation({ duration: 80 });
export default {
	name: 'zRefresh',
	props: {
		isTop: {
			type: Number,
			default: 1
		},
		tip: {
			type: String,
			default: '松开刷新'
		}
	},
	data() {
		return {
			animationData: {},
			isTranf: 0,
			touchStart: '',
			touchMove: '',
			isEnd: 0
		};
	},
	methods: {
		refreshStart(e) {
			if (this.isEnd == 0 && this.isTop == 1) {
				if (e.changedTouches[0]) this.touchStart = e.changedTouches[0].pageY;
			}
		},
		refreshMove(e) {
			if (this.isEnd == 0 && this.isTop == 1 && e.changedTouches[0]) {
				var touchStart = this.touchStart,
					oldMove = this.touchMove,
					newMove = e.changedTouches[0].pageY;
				if (touchStart <= newMove) {
					var isTranf = touchStart > newMove ? 0 : newMove - touchStart;
					this.isTranf = isTranf;
					this.touchMove = e.changedTouches[0].pageY;
				}
			} else {
				this.isTranf = 0;
				this.isEnd = 0;
				this.touchStart = 9999;
			}
		},
		refreshEnd(e) {
			var that = this;
			if (this.isEnd == 0 && this.isTop == 1) {
				if (this.isTranf >= 90) {
					this.isTranf = 125;
					this.isEnd = 1;
					this.$emit('isRefresh');
				} else {
					this.isTranf = 0;
				}
			}
		},
		endAfter() {
			this.isEnd = 2;
			setTimeout(() => {
				this.isTranf = 0;
				this.isEnd = 0;
			}, 600);
		}
	},
	watch: {
		isTranf() {
			let isTranf = this.isTranf > 150 ? 150 : this.isTranf;
			this.animationData = animation
				.translateY(isTranf - 110)
				.step()
				.export();
		}
	},
	computed: {
		isTranform() {
			return '';
			// let isTranf = this.isTranf > 150 ? 150 : this.isTranf;

			// return `transform:translateZ(0) translateY(${isTranf - 110}px);`;
		},
		isZoom() {
			let isTranf = this.isTranf > 125 ? 125 : this.isTranf;
			let zoom = isTranf / 125;
			// return `zoom:${zoom};`;
			return `transform:scale(${zoom});`;
		}
	}
};
</script>

<style lang="less">
@import 'index.less';
